import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import logo from '../assets/logo.png';

function Header({ toggleDarkMode, isDarkMode }) {
  const [menuOpen, setMenuOpen] = useState(false);
  const [searchText, setSearchText] = useState('');

  const buttonStyle = {
    backgroundColor: 'transparent',
    color: isDarkMode ? '#fff' : '#8B0000',
    border: 'none',
    padding: '10px 15px',
    cursor: 'pointer',
    fontWeight: 'bold',
    fontSize: '16px',
  };

  const handleSearch = () => {
    alert(`Searching for: ${searchText}`);
  };

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  return (
    <header
      style={{
        position: 'sticky',
        top: 0,
        zIndex: 1000,
        backgroundColor: isDarkMode ? '#222' : '#fdf9f9ff',
        boxShadow: '0 2px 8px rgba(0, 0, 0, 0.1)',
        transition: 'background-color 0.5s ease',
        padding: '10px 20px',
      }}
    >
      <div style={{
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        flexWrap: 'wrap',
      }}>
        {/* Logo */}
        <div style={{ display: 'flex', alignItems: 'center' }}>
          <img
            src={logo}
            alt="Bharakath Mutton Curry Shop Logo"
            style={{
              height: '50px',
              objectFit: 'contain',
              filter: isDarkMode ? 'brightness(0.85)' : 'none',
            }}
          />
        </div>

        {/* Hamburger icon - visible only on small screens */}
        <button
          onClick={toggleMenu}
          style={{
            display: 'none',
            background: 'transparent',
            border: 'none',
            fontSize: '24px',
            cursor: 'pointer',
            color: isDarkMode ? '#fff' : '#8B0000',
          }}
          className="hamburger-btn"
        >
          â˜°
        </button>

        {/* Center: Search Box */}
        <div style={{
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          flex: 1,
          margin: '10px 0',
          minWidth: '250px'
        }}>
          <input
            type="text"
            value={searchText}
            onChange={(e) => setSearchText(e.target.value)}
            placeholder="Search Varieties..."
            style={{
              padding: '8px 12px',
              borderRadius: '5px 0 0 5px',
              border: '1px solid #ccc',
              outline: 'none',
              width: '200px',
            }}
          />
          <button
            onClick={handleSearch}
            style={{
              padding: '8px 15px',
              backgroundColor: isDarkMode ? '#444' : '#8B0000',
              color: '#fff',
              border: 'none',
              borderRadius: '0 5px 5px 0',
              cursor: 'pointer',
            }}
          >
            Search
          </button>
        </div>

        {/* Navigation Links */}
        <nav className={`nav-links ${menuOpen ? 'open' : ''}`}>
          <Link to="/about"><button style={buttonStyle}>About</button></Link>
          <Link to="/varieties"><button style={buttonStyle}>Varieties</button></Link>
          <Link to="/admin-login"><button style={buttonStyle}>Admin Orders</button></Link>
        </nav>
      </div>

      {/* Styles for responsive behavior */}
      <style>{`
        @media (max-width: 768px) {
          .hamburger-btn {
            display: block;
          }
          .nav-links {
            display: ${menuOpen ? 'flex' : 'none'};
            flex-direction: column;
            align-items: flex-start;
            width: 100%;
            margin-top: 10px;
            padding-top: 10px;
            border-top: 1px solid ${isDarkMode ? '#444' : '#ccc'};
          }
          .nav-links a {
            margin: 5px 0;
          }
        }
        @media (min-width: 769px) {
          .nav-links {
            display: flex;
            gap: 15px;
            align-items: center;
          }
        }
      `}</style>
    </header>
  );
}

export default Header;
