import React, { useState } from 'react';

const curryList = [
  { id: 1, name: 'Spicy Mutton Gravy', category: 'Gravy', price: 220 },
  { id: 2, name: 'Dry Mutton Fry', category: 'Dry', price: 250 },
  { id: 3, name: 'Mutton Chukka', category: 'Dry', price: 270 },
  { id: 4, name: 'Mutton Rogan Josh', category: 'Gravy', price: 300 },
];

const Menu = ({ onOrderClick }) => {
  const [search, setSearch] = useState('');

  const filtered = curryList.filter(
    (item) => item.name.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div>
      <input
        type="text"
        placeholder="Search curry..."
        value={search}
        onChange={(e) => setSearch(e.target.value)}
        style={{ padding: '8px', width: '100%', marginBottom: '20px' }}
      />

      <div style={{ display: 'grid', gap: '20px' }}>
        {filtered.map((item) => (
          <div key={item.id} style={{ border: '1px solid #ccc', padding: '15px' }}>
            <h3>{item.name}</h3>
            <p>Category: {item.category}</p>
            <p>Price: â‚¹{item.price}</p>
            <button onClick={() => onOrderClick(item)}>Order Now</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Menu;
