import React from 'react';

function OrderTimeline() {
const steps = [
  { id: 1, icon: 'üõí', title: 'Order Placed', description: 'Your mutton curry order is placed.' },
  { id: 2, icon: '‚úÖ', title: 'Order Confirmed', description: 'We have confirmed your order.' },
  { id: 3, icon: 'üç≤', title: 'Cooking', description: 'Our chef is preparing your curry.' },
  { id: 4, icon: 'üì¶', title: 'Ready for Pickup', description: 'Your order is ready for pickup or delivery.' },
  { id: 5, icon: 'üòã', title: 'Order Completed', description: 'Enjoy your delicious curry!' },
];


  return (
    <section style={{ padding: '40px 20px', backgroundColor: '#fff' }}>
      <h2 style={{ textAlign: 'center', marginBottom: '30px' }}>How Our Order System Works</h2>
      <div style={{ maxWidth: '800px', margin: '0 auto' }}>
        {steps.map((step, index) => (
          <div
            key={step.id}
            style={{
              borderLeft: '4px solid #B22222',
              padding: '10px 20px',
              marginBottom: '25px',
              position: 'relative',
            }}
          >
            <div
              style={{
                position: 'absolute',
                left: '-14px',
                top: '10px',
                backgroundColor: '#B22222',
                color: '#fff',
                width: '24px',
                height: '24px',
                borderRadius: '50%',
                textAlign: 'center',
                lineHeight: '24px',
                fontWeight: 'bold',
              }}
            >
              {step.id}
            </div>
            <h3 style={{ marginBottom: '5px' }}>{step.title}</h3>
            <p style={{ margin: 0 }}>{step.description}</p>
          </div>
        ))}
      </div>
    </section>
  );
}

export default OrderTimeline;
