import React, { useState, useEffect, useRef } from 'react';

const LiveChat = () => {
  const [phone, setPhone] = useState('');
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const chatEndRef = useRef(null);

  useEffect(() => {
    if (phone) fetchMessages();
    // eslint-disable-next-line
  }, [phone]);

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const fetchMessages = async () => {
    setLoading(true);
    setError('');
    try {
      const res = await fetch(`http://localhost:5000/api/chat?phone=${phone}`);
      const data = await res.json();
      if (res.ok) setMessages(data);
      else setError(data.error || 'Failed to fetch chat');
    } catch {
      setError('Network error');
    }
    setLoading(false);
  };

  const sendMessage = async (e) => {
    e.preventDefault();
    if (!input.trim()) return;
    setLoading(true);
    setError('');
    try {
      const res = await fetch('http://localhost:5000/api/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ phone, message: input, timestamp: new Date().toISOString() })
      });
      const data = await res.json();
      if (res.ok) {
        setInput('');
        fetchMessages();
      } else setError(data.error || 'Failed to send message');
    } catch {
      setError('Network error');
    }
    setLoading(false);
  };

  return (
    <div style={{ position: 'fixed', bottom: 20, right: 20, zIndex: 9999, maxWidth: 340, width: '90vw', boxShadow: '0 2px 12px #B2222244', borderRadius: 16, background: '#fff', fontFamily: 'Segoe UI, Arial, sans-serif' }}>
      <div style={{ background: '#FFD700', color: '#B22222', padding: '10px 16px', borderTopLeftRadius: 16, borderTopRightRadius: 16, fontWeight: 'bold', fontSize: '1.1rem' }}>
        ðŸ’¬ Live Chat Support
      </div>
      <div style={{ padding: '12px', minHeight: 180, maxHeight: 260, overflowY: 'auto', background: '#f9f9ff', borderBottom: '1px solid #FFD700' }}>
        {!phone ? (
          <div style={{ textAlign: 'center', color: '#888' }}>
            <input
              type="tel"
              value={phone}
              onChange={e => setPhone(e.target.value)}
              placeholder="Enter your phone to start chat"
              style={{ padding: '8px', borderRadius: '8px', border: '1px solid #FFD700', fontSize: '1rem', minWidth: '180px' }}
              maxLength={10}
            />
            <div style={{ marginTop: 8, fontSize: '0.95rem', color: '#B22222' }}>We use your phone to show your chat history.</div>
          </div>
        ) : (
          <>
            {loading && <div style={{ textAlign: 'center', color: '#888' }}>Loading...</div>}
            {error && <div style={{ textAlign: 'center', color: 'red' }}>{error}</div>}
            {messages.length === 0 && !loading && <div style={{ textAlign: 'center', color: '#888' }}>No messages yet.</div>}
            {messages.map((msg, idx) => (
              <div key={idx} style={{ marginBottom: 8, textAlign: 'left' }}>
                <span style={{ background: '#FFD700', color: '#B22222', borderRadius: 8, padding: '4px 8px', fontSize: '0.98rem', display: 'inline-block' }}>
                  {msg.message}
                </span>
                <span style={{ fontSize: '0.8rem', color: '#888', marginLeft: 8 }}>{new Date(msg.timestamp).toLocaleTimeString()}</span>
              </div>
            ))}
            <div ref={chatEndRef} />
          </>
        )}
      </div>
      {phone && (
        <form onSubmit={sendMessage} style={{ display: 'flex', padding: '10px', gap: 8 }}>
          <input
            type="text"
            value={input}
            onChange={e => setInput(e.target.value)}
            placeholder="Type your message..."
            style={{ flex: 1, padding: '8px', borderRadius: '8px', border: '1px solid #FFD700', fontSize: '1rem' }}
            maxLength={200}
          />
          <button
            type="submit"
            style={{ background: '#FFD700', color: '#B22222', border: 'none', borderRadius: '8px', padding: '8px 14px', fontWeight: 'bold', fontSize: '1rem', cursor: 'pointer' }}
          >
            âž¤
          </button>
        </form>
      )}
    </div>
  );
};

export default LiveChat;
