import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  Grid, Card, CardContent, CardMedia, Typography, Container,
  TextField, CircularProgress, Tabs, Tab, Box
} from '@mui/material';

const categories = ['All', 'Spicy', 'Dry', 'Gravy']; // üîÅ Add more if needed

function Menu() {
  const [menuItems, setMenuItems] = useState([]);
  const [filteredItems, setFilteredItems] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('All');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios.get('http://localhost:5000/menu')
      .then(res => {
        setMenuItems(res.data);
        setFilteredItems(res.data);
        setLoading(false);
      })
      .catch(err => {
        console.error('‚ùå Error fetching menu:', err);
        setLoading(false);
      });
  }, []);

  useEffect(() => {
    const query = searchQuery.toLowerCase();

    const filtered = menuItems.filter(item => {
      const matchCategory = selectedCategory === 'All' || item.category === selectedCategory;
      const matchSearch = item.name.toLowerCase().includes(query) || item.category.toLowerCase().includes(query);
      return matchCategory && matchSearch;
    });

    setFilteredItems(filtered);
  }, [searchQuery, selectedCategory, menuItems]);

  return (
    <Container sx={{ paddingTop: 4, paddingBottom: 4 }}>
      <Typography variant="h4" gutterBottom align="center">
        üçñ Explore Our Mutton Curries
      </Typography>

      {/* üîç Search Bar */}
      <TextField
        fullWidth
        label="Search by name or category"
        variant="outlined"
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
        sx={{ marginBottom: 2 }}
      />

      {/* üóÇÔ∏è Category Tabs */}
      <Box sx={{ marginBottom: 3 }}>
        <Tabs
          value={selectedCategory}
          onChange={(e, newVal) => setSelectedCategory(newVal)}
          variant="scrollable"
          scrollButtons="auto"
        >
          {categories.map(cat => (
            <Tab key={cat} value={cat} label={cat} />
          ))}
        </Tabs>
      </Box>

      {/* üßæ Menu Items */}
      {loading ? (
        <Grid container justifyContent="center">
          <CircularProgress />
        </Grid>
      ) : (
        <Grid container spacing={4}>
          {filteredItems.length === 0 ? (
            <Typography variant="body1">No items found.</Typography>
          ) : (
            filteredItems.map(item => (
              <Grid item xs={12} sm={6} md={4} key={item.id}>
                <Card sx={{ height: '100%' }}>
                  <CardMedia
                    component="img"
                    height="180"
                    image={item.image_url || 'https://via.placeholder.com/300x180?text=Mutton+Curry'}
                    alt={item.name}
                  />
                  <CardContent>
                    <Typography variant="h6">{item.name}</Typography>
                    <Typography variant="body2" color="text.secondary">
                      Category: {item.category} | Price: ‚Çπ{item.price}
                    </Typography>
                  </CardContent>
                </Card>
              </Grid>
            ))
          )}
        </Grid>
      )}
    </Container>
  );
}

export default Menu;
