import React, { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

const productData = [
  { id: 1, name: 'Normal Mutton', price: '‚Çπ800', type: 'Spicy', description: 'A classic spicy mutton curry with bones.' },
  { id: 2, name: 'BoneLess Mutton', price: '‚Çπ1000', type: 'Gravy', description: 'Soft, juicy boneless mutton pieces in rich gravy.' },
  { id: 3, name: 'Mutton Kudal', price: '‚Çπ280', type: 'Spicy', description: 'Traditional South Indian spicy kudal mutton.' },
  { id: 4, name: 'Mutton Thalai', price: '‚Çπ300', type: 'Gravy', description: 'Delicious head meat in flavorful masala gravy.' },
  { id: 5, name: 'Mutton Kaal', price: '‚Çπ200', type: 'Soup', description: 'Spicy and healthy goat leg soup (paaya).' },
];


const OrderPage = () => {
  const { productId } = useParams();
  const product = productData.find(p => p.id === parseInt(productId));

  const [name, setName] = useState('');
  const [phone, setPhone] = useState('');
  const [quantity, setQuantity] = useState('');
  const [address, setAddress] = useState('');
  const [loading, setLoading] = useState(false);
  const [showSuccess, setShowSuccess] = useState(false);
  const [orderDetails, setOrderDetails] = useState(null);

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!name || !phone || !quantity || !address) {
      alert('Please fill all fields.');
      return;
    }

    const orderData = {
      name,
      phone,
      quantity,
      address,
      item_name: product.name
    };

    setLoading(true);

    fetch('http://localhost:5000/api/orders', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(orderData)
    })
      .then(res => res.json())
      .then(data => {
        setLoading(false);
        setOrderDetails({
          ...orderData,
          order_id: data.order_id
        });
        setShowSuccess(true);
        setName(''); setPhone(''); setQuantity(''); setAddress('');
      })
      .catch(err => {
        setLoading(false);
        console.error('‚ùå Order failed:', err);
        alert('‚ùå Something went wrong. Please try again.');
      });
  };

  if (!product) {
    return (
      <div style={{ padding: '40px', fontFamily: 'Arial' }}>
        <h2 style={{ color: '#B22222' }}>Product Not Found</h2>
      </div>
    );
  }

  return (
    <div style={{
      padding: '40px',
      fontFamily: 'Segoe UI, sans-serif',
      backgroundColor: '#f9f9f9',
      minHeight: '100vh'
    }}>
      <div style={{
        maxWidth: '700px',
        margin: 'auto',
        backgroundColor: '#fff',
        padding: '30px',
        borderRadius: '10px',
        boxShadow: '0 0 15px rgba(0,0,0,0.1)'
      }}>
        <h2 style={{ textAlign: 'center', color: '#B22222' }}>Order: {product.name}</h2>
        <p><strong>Type:</strong> {product.type}</p>
        <p><strong>Price:</strong> {product.price}</p>
        <p><strong>Description:</strong> {product.description}</p>

        <hr style={{ margin: '20px 0' }} />

        <form onSubmit={handleSubmit}>
          <div style={{ marginBottom: '15px' }}>
            <label style={labelStyle}>üë§ Name:</label>
            <input
              type="text"
              value={name}
              onChange={e => setName(e.target.value)}
              required
              style={inputStyle}
            />
          </div>
          <div style={{ marginBottom: '15px' }}>
            <label style={labelStyle}>üìû Phone Number:</label>
            <input
              type="tel"
              value={phone}
              onChange={e => setPhone(e.target.value)}
              required
              style={inputStyle}
            />
          </div>
          <div style={{ marginBottom: '15px' }}>
            <label style={labelStyle}>ü•Ñ Quantity (Kg or Nos):</label>
            <input
              type="text"
              value={quantity}
              onChange={e => setQuantity(e.target.value)}
              required
              style={inputStyle}
            />
          </div>
          <div style={{ marginBottom: '20px' }}>
            <label style={labelStyle}>üìç Delivery Address:</label>
            <textarea
              value={address}
              onChange={e => setAddress(e.target.value)}
              required
              style={{ ...inputStyle, height: '80px' }}
            />
          </div>

          <button type="submit" style={submitButtonStyle} disabled={loading}>
            {loading ? "Placing Order..." : "‚úÖ Place Order"}
          </button>
        </form>
        {/* Success Modal Dialog */}
        {showSuccess && orderDetails && (
          <div style={{
            position: 'fixed',
            top: 0,
            left: 0,
            width: '100vw',
            height: '100vh',
            background: 'rgba(0,0,0,0.35)',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            zIndex: 9999
          }}>
            <div style={{
              background: '#fff',
              borderRadius: '16px',
              padding: '32px 24px',
              boxShadow: '0 2px 16px #FFD70044',
              maxWidth: '400px',
              textAlign: 'center',
              position: 'relative'
            }}>
              <h2 style={{ color: '#25D366', marginBottom: '12px' }}>‚úÖ Order Placed Successfully!</h2>
              <p style={{ fontSize: '1.1rem', marginBottom: '10px' }}>Thank you, <strong>{orderDetails.name}</strong>!</p>
              <p>Your order for <strong>{orderDetails.item_name}</strong> (<strong>{orderDetails.quantity}</strong>) has been received.</p>
              <p><strong>Order ID:</strong> <span style={{ color: '#B22222' }}>{orderDetails.order_id}</span></p>
              <p><strong>Delivery Address:</strong> {orderDetails.address}</p>
              <p><strong>Phone:</strong> {orderDetails.phone}</p>
              <button
                style={{
                  marginTop: '18px',
                  background: 'linear-gradient(90deg, #FFD700 60%, #25D366 100%)',
                  color: '#fff',
                  padding: '10px 28px',
                  borderRadius: '30px',
                  fontWeight: 'bold',
                  fontSize: '1.1rem',
                  border: 'none',
                  cursor: 'pointer',
                  boxShadow: '0 2px 8px #FFD70044',
                  transition: 'background 0.3s',
                }}
                onClick={() => setShowSuccess(false)}
              >
                Close
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

// Styles
const labelStyle = {
  display: 'block',
  marginBottom: '6px',
  fontWeight: '600',
  color: '#333'
};

const inputStyle = {
  width: '100%',
  padding: '10px',
  borderRadius: '6px',
  border: '1px solid #ccc',
  fontSize: '15px'
};

const submitButtonStyle = {
  backgroundColor: '#B22222',
  color: 'white',
  padding: '12px 20px',
  border: 'none',
  borderRadius: '6px',
  fontSize: '16px',
  cursor: 'pointer',
  width: '100%'
};

export default OrderPage;
