import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  Container,
  Grid,
  Card,
  CardContent,
  CardMedia,
  Typography,
  CircularProgress,
  Box,
  Paper
} from '@mui/material';

const curryImages = {
  "Mutton Rogan Josh": "https://your-image-link.com/roganjosh.jpg",
  "Mutton Biryani": "https://your-image-link.com/biryani.jpg",
  "Mutton Korma": "https://your-image-link.com/korma.jpg",
  "Mutton Sukka": "https://your-image-link.com/sukka.jpg",
  "Mutton Keema": "https://your-image-link.com/keema.jpg",
  // fallback for items without image
  default: "https://your-image-link.com/default-mutton.jpg"
};

const ViewMenu = () => {
  const [menu, setMenu] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios.get('http://localhost:5000/menu')
      .then(res => {
        setMenu(res.data);
        setLoading(false);
      })
      .catch(err => {
        console.error('Error fetching menu:', err);
        setLoading(false);
      });
  }, []);

  return (
    <Container sx={{ mt: 5 }}>
      <Typography variant="h4" gutterBottom align="center">
        üçõ Our Mutton Curry Menu
      </Typography>

      {loading ? (
        <Box sx={{ display: 'flex', justifyContent: 'center', mt: 5 }}>
          <CircularProgress />
        </Box>
      ) : (
        <Grid container spacing={4}>
          {menu.map((item, idx) => (
            <Grid item key={idx} xs={12} sm={6} md={4}>
              <Card elevation={3}>
                <CardMedia
                  component="img"
                  height="180"
                  image={curryImages[item.name] || curryImages.default}
                  alt={item.name}
                />
                <CardContent>
                  <Typography variant="h6" gutterBottom>
                    {item.name}
                  </Typography>
                  <Typography variant="body1" color="text.secondary">
                    ‚Çπ{item.price} / Kg
                  </Typography>
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>
      )}
    </Container>
  );
};

export default ViewMenu;
