import React, { useEffect, useState } from 'react';

const AdminOrders = () => {
  const [orders, setOrders] = useState([]);

  // Fetch orders from backend
  useEffect(() => {
    fetch('http://localhost:5000/api/orders')
      .then(res => res.json())
      .then(data => setOrders(data))
      .catch(err => console.error("Error fetching orders:", err));
  }, []);

  // Clear all orders
  const clearAllOrders = () => {
    if (window.confirm("Are you sure you want to delete all orders?")) {
      fetch('http://localhost:5000/api/orders', {
        method: 'DELETE',
      })
        .then(res => res.json())
        .then(() => setOrders([]))
        .catch(err => console.error("Error deleting orders:", err));
    }
  };

  // Update order status
  const updateOrderStatus = (orderId, newStatus) => {
    fetch(`http://localhost:5000/api/orders/${orderId}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ status: newStatus }),
    })
      .then(res => res.json())
      .then(updatedOrder => {
        const updatedOrders = orders.map(order =>
          order.id === orderId ? { ...order, status: newStatus } : order
        );
        setOrders(updatedOrders);
      })
      .catch(err => console.error('Error updating status:', err));
  };

  return (
    <div style={{ padding: '16px', fontFamily: 'Segoe UI, Arial, sans-serif', background: '#f9f9ff', minHeight: '100vh' }}>
      <h2 style={{ textAlign: 'center', color: '#B22222', fontWeight: 'bold', fontSize: '2rem', marginBottom: '18px' }}>ðŸ“‹ All Orders</h2>

      {orders.length === 0 ? (
        <p style={{ textAlign: 'center', color: '#888', fontSize: '1.1rem' }}>No orders placed yet.</p>
      ) : (
        <div style={{
          overflowX: 'auto',
          borderRadius: '16px',
          boxShadow: '0 2px 12px rgba(178,34,34,0.07)',
          background: '#fff',
        }}>
          <table style={{ width: '100%', borderCollapse: 'collapse', minWidth: '700px' }}>
            <thead style={{ backgroundColor: '#FFD700' }}>
              <tr>
                <th style={cellStyle}>Name</th>
                <th style={cellStyle}>Phone</th>
                <th style={cellStyle}>Address</th>
                <th style={cellStyle}>Item</th>
                <th style={cellStyle}>Qty</th>
                <th style={cellStyle}>Order Time</th>
                <th style={cellStyle}>Status</th>
              </tr>
            </thead>
            <tbody>
              {orders.map((order, index) => (
                <tr key={index} style={{ background: index % 2 === 0 ? '#fff' : '#f9f9ff' }}>
                  <td style={cellStyle}>{order.name}</td>
                  <td style={cellStyle}>{order.phone}</td>
                  <td style={cellStyle}>{order.address}</td>
                  <td style={cellStyle}>{order.item_name}</td>
                  <td style={cellStyle}>{order.quantity}</td>
                  <td style={cellStyle}>{new Date(order.order_time).toLocaleString()}</td>
                  <td style={cellStyle}>
                    <span style={{
                      display: 'inline-block',
                      padding: '6px 12px',
                      borderRadius: '12px',
                      fontWeight: 'bold',
                      fontSize: '0.95rem',
                      background: order.status === 'Delivered' ? '#25D366' : order.status === 'Out for Delivery' ? '#FFD700' : order.status === 'Preparing' ? '#B22222' : '#ccc',
                      color: order.status === 'Delivered' ? '#fff' : order.status === 'Out for Delivery' ? '#B22222' : '#fff',
                      marginRight: '8px',
                      transition: 'background 0.3s',
                    }}>{order.status}</span>
                    <select
                      value={order.status}
                      onChange={(e) => updateOrderStatus(order.id, e.target.value)}
                      style={{
                        padding: '6px 12px',
                        borderRadius: '8px',
                        border: '1px solid #FFD700',
                        fontWeight: 'bold',
                        fontSize: '0.95rem',
                        background: '#fff',
                        color: '#B22222',
                        cursor: 'pointer',
                        marginTop: '4px',
                        minWidth: '120px',
                      }}
                    >
                      <option value="Pending">Pending</option>
                      <option value="Preparing">Preparing</option>
                      <option value="Out for Delivery">Out for Delivery</option>
                      <option value="Delivered">Delivered</option>
                    </select>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      <div style={{ textAlign: 'center', marginTop: '24px' }}>
        <button
          onClick={clearAllOrders}
          style={{
            background: 'linear-gradient(90deg, #B22222 60%, #FFD700 100%)',
            color: '#fff',
            padding: '12px 28px',
            border: 'none',
            borderRadius: '30px',
            cursor: 'pointer',
            fontSize: '1.1rem',
            fontWeight: 'bold',
            boxShadow: '0 2px 8px #FFD70044',
            transition: 'background 0.3s',
          }}
        >
          ðŸ§¹ Clear All Orders
        </button>
      </div>

      {/* Mobile-friendly tips */}
      <div style={{ textAlign: 'center', marginTop: '18px', color: '#888', fontSize: '0.95rem' }}>
        <span role="img" aria-label="swipe">ðŸ‘†</span> Swipe left/right to view all columns on mobile
      </div>
    </div>
  );
};

const cellStyle = {
  border: '1px solid #ccc',
  padding: '10px',
  textAlign: 'center',
};

export default AdminOrders;
