import React from 'react';

const reviews = [
  {
    name: 'Aarav S.',
    text: 'Best mutton curry I have ever tasted! Super fresh and delivered on time.',
    rating: 5
  },
  {
    name: 'Priya R.',
    text: 'Loved the hygiene and quality. My family enjoyed every bite!',
    rating: 5
  },
  {
    name: 'Vikram T.',
    text: 'Good packaging and great taste. Highly recommended!',
    rating: 4
  }
];

const CustomerReviews = () => (
  <section style={{
    background: '#fff',
    borderRadius: '16px',
    boxShadow: '0 2px 12px rgba(0,0,0,0.07)',
    maxWidth: '900px',
    margin: '30px auto',
    padding: '24px',
    textAlign: 'center',
  }}>
    <h2 style={{ color: '#B22222', marginBottom: '18px' }}>Customer Reviews</h2>
    <div style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'center', gap: '18px' }}>
      {reviews.map((review, idx) => (
        <div key={idx} style={{
          background: '#f9f9ff',
          borderRadius: '12px',
          padding: '18px',
          minWidth: '220px',
          maxWidth: '280px',
          boxShadow: '0 1px 6px rgba(178,34,34,0.07)',
        }}>
          <div style={{ fontWeight: 'bold', color: '#B22222', marginBottom: '6px' }}>{review.name}</div>
          <div style={{ fontSize: '1rem', marginBottom: '8px' }}>{review.text}</div>
          <div style={{ color: '#FFD700', fontSize: '1.2rem' }}>{'★'.repeat(review.rating)}{'☆'.repeat(5 - review.rating)}</div>
        </div>
      ))}
    </div>
  </section>
);

export default CustomerReviews;
