import React from 'react';
import './Highlights.css';

const Highlights = () => {
  return (
    <section
      style={{
        display: 'flex',
        flexWrap: 'wrap',
        justifyContent: 'center',
        alignItems: 'center',
        gap: '32px',
        padding: '32px 10px',
        background: 'linear-gradient(90deg, #fff0f0 60%, #f9f9ff 100%)',
        borderRadius: '24px',
        boxShadow: '0 4px 24px rgba(178,34,34,0.07)',
        margin: '24px auto',
        maxWidth: '900px',
      }}
    >
      <div style={{
        background: '#fff',
        borderRadius: '18px',
        boxShadow: '0 2px 12px rgba(178,34,34,0.09)',
        padding: '24px',
        minWidth: '220px',
        textAlign: 'center',
        animation: 'fadeIn 1s',
      }}>
        <h2 style={{
          color: '#B22222',
          fontWeight: 'bold',
          fontSize: '1.3rem',
          marginBottom: '10px',
          letterSpacing: '1px',
          animation: 'blinker 1.5s linear infinite',
        }}>Best Hygiene & Health Standards</h2>
        <span style={{
          display: 'inline-block',
          background: 'linear-gradient(90deg, #B22222 60%, #fff0f0 100%)',
          color: '#fff',
          borderRadius: '12px',
          padding: '8px 18px',
          fontWeight: 'bold',
          fontSize: '1rem',
          boxShadow: '0 2px 8px rgba(178,34,34,0.12)',
          marginBottom: '8px',
          animation: 'bounce 1.2s infinite',
        }}>Next Day Fresh Delivery</span>
      </div>
      <div style={{
        background: '#fff',
        borderRadius: '18px',
        boxShadow: '0 2px 12px rgba(178,34,34,0.09)',
        padding: '24px',
        minWidth: '220px',
        textAlign: 'center',
        animation: 'fadeIn 1.2s',
      }}>
        <h2 style={{
          color: '#B22222',
          fontWeight: 'bold',
          fontSize: '1.3rem',
          marginBottom: '10px',
          letterSpacing: '1px',
          textShadow: '0 0 8px #B22222',
          animation: 'glow 2s infinite alternate',
        }}>Halal Certified Quality</h2>
        <span style={{
          display: 'inline-block',
          background: 'linear-gradient(90deg, #FFD700 60%, #fff0f0 100%)',
          color: '#B22222',
          borderRadius: '12px',
          padding: '8px 18px',
          fontWeight: 'bold',
          fontSize: '1rem',
          boxShadow: '0 2px 8px rgba(178,34,34,0.12)',
          marginBottom: '8px',
          animation: 'pulse 1.5s infinite',
        }}>100% Refund Guarantee</span>
      </div>
      <style>{`
        @keyframes blinker {
          50% { opacity: 0.5; }
        }
        @keyframes bounce {
          0%, 100% { transform: translateY(0); }
          50% { transform: translateY(-10px); }
        }
        @keyframes glow {
          0% { text-shadow: 0 0 8px #B22222; }
          100% { text-shadow: 0 0 24px #FFD700; }
        }
        @keyframes pulse {
          0% { box-shadow: 0 0 0 0 #FFD70044; }
          70% { box-shadow: 0 0 0 12px #FFD70022; }
          100% { box-shadow: 0 0 0 0 #FFD70044; }
        }
        @keyframes fadeIn {
          from { opacity: 0; transform: translateY(20px); }
          to { opacity: 1; transform: translateY(0); }
        }
      `}</style>
    </section>
  );
};

export default Highlights;
