import React from 'react';

const products = [
  { id: 1, name: 'Normal Mutton', price: '₹800', type: 'Spicy' },
  { id: 2, name: 'BoneLess Mutton', price: '₹1000', type: 'Gravy' },
  { id: 3, name: 'Mutton Kudal', price: '₹280', type: 'Spicy' },
  { id: 4, name: 'Mutton Thalai', price: '₹300', type: 'Gravy' },
  { id: 5, name: 'Mutton Kaal', price: '₹200', type: 'Soup' },
];

function ProductList() {
  return (
    <section style={{ padding: '30px', backgroundColor: '#fff' }}>
      <h2 style={{ textAlign: 'center' }}>Our Mutton Curry Varieties</h2>
      <div style={{
        display: 'flex',
        flexWrap: 'wrap',
        justifyContent: 'center',
        marginTop: '20px'
      }}>
        {products.map((item) => (
          <div key={item.id} style={{
            border: '1px solid #ccc',
            borderRadius: '10px',
            padding: '15px',
            margin: '10px',
            width: '220px',
            boxShadow: '0 0 10px rgba(0,0,0,0.1)',
            textAlign: 'center'
          }}>
            <h3>{item.name}</h3>
            <p>Type: {item.type}</p>
            <p><strong>Price: {item.price}</strong></p>
            <button style={{
              backgroundColor: '#B22222',
              color: 'white',
              padding: '10px 15px',
              border: 'none',
              borderRadius: '5px',
              cursor: 'pointer'
            }}>
              Order Now
            </button>
          </div>
        ))}
      </div>
    </section>
  );
}

export default ProductList;
