// src/pages/OrderStatusPage.js
import React, { useState, useEffect } from 'react';
import OrderStatusTimeline from '../components/OrderStatusTimeline';

function OrderStatusPage() {
  const [orderStatus, setOrderStatus] = useState('Order Received');

  // Simulate backend status changes
  useEffect(() => {
    const statusFlow = ['Order Received', 'Preparing', 'Out for Delivery', 'Delivered'];
    let step = 0;

    const interval = setInterval(() => {
      if (step < statusFlow.length) {
        setOrderStatus(statusFlow[step]);
        step += 1;
      } else {
        clearInterval(interval);
      }
    }, 3000); // change every 3 seconds

    return () => clearInterval(interval);
  }, []);

  return (
    <div>
      <h2 style={{ textAlign: 'center', marginTop: '20px' }}>Track Your Order</h2>
      <OrderStatusTimeline currentStatus={orderStatus} />
    </div>
  );
}

export default OrderStatusPage;
