// frontend/src/components/Header.js
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import logo from '../assets/logo.png';

function Header({ toggleDarkMode, isDarkMode }) {
  const [menuOpen, setMenuOpen] = useState(false);

  const buttonStyle = {
    backgroundColor: 'transparent',
    color: isDarkMode ? '#fff' : '#8B0000',
    border: 'none',
    padding: '10px 15px',
    cursor: 'pointer',
    fontWeight: 'bold',
    fontSize: '16px',
  };

  return (
    <header
      style={{
        position: 'sticky',
        top: 0,
        zIndex: 1000,
        backgroundColor: isDarkMode ? '#222' : '#fdf9f9ff',
        display: 'flex',
        justifyContent: 'flex-start',
        alignItems: 'center',
        padding: '10px 20px',
        boxShadow: '0 2px 8px rgba(0, 0, 0, 0.1)',
        transition: 'background-color 0.5s ease',
        flexWrap: 'wrap',
      }}
    >
      {/* Hamburger Menu Button for Mobile */}
      <div className="hamburger" onClick={() => setMenuOpen(!menuOpen)} style={{ display: 'none' }}>
        <div style={{ width: 25, height: 3, backgroundColor: isDarkMode ? '#fff' : '#8B0000', margin: '4px 0' }}></div>
        <div style={{ width: 25, height: 3, backgroundColor: isDarkMode ? '#fff' : '#8B0000', margin: '4px 0' }}></div>
        <div style={{ width: 25, height: 3, backgroundColor: isDarkMode ? '#fff' : '#8B0000', margin: '4px 0' }}></div>
      </div>

      {/* Nav Links */}
      <nav style={{ display: menuOpen ? 'flex' : 'flex', gap: '15px', flexWrap: 'wrap' }}>
        <Link to="/about"><button style={buttonStyle}>About</button></Link>
        <Link to="/varieties"><button style={buttonStyle}>Varieties</button></Link>
        <Link to="/admin-login"><button style={buttonStyle}>Admin Orders</button></Link>
      </nav>

      {/* Logo */}
      <img
        src={logo}
        alt="Bharakath Mutton Curry Shop Logo"
        style={{ height: '50px', marginLeft: 'auto' }}
      />
    </header>
  );
}

export default Header;
