import React, { useState } from 'react';
import Menu from './components/Menu';
import OrderForm from './components/OrderForm';
import AdminPanel from './components/AdminPanel';

function App() {
  const [selectedCurry, setSelectedCurry] = useState(null);
  const [orders, setOrders] = useState([]);

  const handleOrderClick = (curry) => {
    setSelectedCurry(curry);
  };

  const handleOrderSubmit = (orderData) => {
    setOrders([...orders, orderData]);
    setSelectedCurry(null);
  };

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial' }}>
      <div style={{ display: 'flex', justifyContent: 'flex-end', marginBottom: '20px' }}>
        <AdminPanel orders={orders} />
      </div>

      <h1>üçõ Mutton Curry Varieties</h1>
      <Menu onOrderClick={handleOrderClick} />

      {selectedCurry && (
        <OrderForm curry={selectedCurry} onSubmit={handleOrderSubmit} onCancel={() => setSelectedCurry(null)} />
      )}
    </div>
  );
}

export default App;
