import React, { useEffect, useState } from 'react';

const AdminOrders = () => {
  const [orders, setOrders] = useState([]);
  const [statusMap, setStatusMap] = useState({});

  const statusOptions = [
    'Order Received',
    'Cooking',
    'Ready for Pickup',
    'Out for Delivery',
    'Delivered',
  ];

  // Fetch orders
  useEffect(() => {
    fetchOrders();
  }, []);

  const fetchOrders = () => {
    fetch('http://localhost:5000/api/orders')
      .then(res => res.json())
      .then(data => {
        setOrders(data);
        const initialStatus = {};
        data.forEach(order => {
          initialStatus[order.id] = order.status;
        });
        setStatusMap(initialStatus);
      })
      .catch(err => console.error("Error fetching orders:", err));
  };

  // Clear all orders
  const clearAllOrders = () => {
    if (window.confirm("Are you sure you want to delete all orders?")) {
      fetch('http://localhost:5000/api/orders', {
        method: 'DELETE',
      })
        .then(res => res.json())
        .then(() => setOrders([]))
        .catch(err => console.error("Error deleting orders:", err));
    }
  };

  // Handle status change
  const handleStatusChange = (orderId, newStatus) => {
    fetch(`http://localhost:5000/api/update-status/${orderId}`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ status: newStatus }),
    })
      .then(res => res.json())
      .then(() => {
        setStatusMap(prev => ({ ...prev, [orderId]: newStatus }));
      })
      .catch(err => console.error("Error updating status:", err));
  };

  return (
    <div style={{ padding: '30px', fontFamily: 'Arial' }}>
      <h2 style={{ textAlign: 'center', color: '#8B0000' }}>ðŸ“‹ All Orders</h2>

      {orders.length === 0 ? (
        <p style={{ textAlign: 'center' }}>No orders placed yet.</p>
      ) : (
        <table style={{ width: '100%', borderCollapse: 'collapse', marginTop: '20px' }}>
          <thead style={{ backgroundColor: '#f5f5f5' }}>
            <tr>
              <th style={cellStyle}>Customer Name</th>
              <th style={cellStyle}>Phone</th>
              <th style={cellStyle}>Address</th>
              <th style={cellStyle}>Item</th>
              <th style={cellStyle}>Quantity</th>
              <th style={cellStyle}>Order Time</th>
              <th style={cellStyle}>Status</th>
            </tr>
          </thead>
          <tbody>
            {orders.map((order, index) => (
              <tr key={index}>
                <td style={cellStyle}>{order.name}</td>
                <td style={cellStyle}>{order.phone}</td>
                <td style={cellStyle}>{order.address}</td>
                <td style={cellStyle}>{order.item_name}</td>
                <td style={cellStyle}>{order.quantity}</td>
                <td style={cellStyle}>{new Date(order.order_time).toLocaleString()}</td>
                <td style={cellStyle}>
                  <select
                    value={statusMap[order.id] || order.status}
                    onChange={(e) => handleStatusChange(order.id, e.target.value)}
                  >
                    {statusOptions.map((status, idx) => (
                      <option key={idx} value={status}>{status}</option>
                    ))}
                  </select>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      <div style={{ textAlign: 'center', marginTop: '20px' }}>
        <button
          onClick={clearAllOrders}
          style={{
            backgroundColor: 'crimson',
            color: '#fff',
            padding: '10px 20px',
            border: 'none',
            borderRadius: '5px',
            cursor: 'pointer',
            fontSize: '16px',
          }}
        >
          ðŸ§¹ Clear All Orders
        </button>
      </div>
    </div>
  );
};

const cellStyle = {
  border: '1px solid #ccc',
  padding: '10px',
  textAlign: 'center',
};

export default AdminOrders;
