import React, { useState } from "react";
import { Button, Box, Typography, Modal, Grid } from "@mui/material";
import Menu from "./Menu"; // your dummy data array
import OrderForm from "./OrderForm";
import AdminPanel from "./AdminPanel";

const HomePage = () => {
  const [selectedItem, setSelectedItem] = useState(null);
  const [showOrderForm, setShowOrderForm] = useState(false);
  const [showAdmin, setShowAdmin] = useState(false);

  const handleOrderClick = (item) => {
    setSelectedItem(item);
    setShowOrderForm(true);
  };

  const handleCloseForm = () => {
    setSelectedItem(null);
    setShowOrderForm(false);
  };

  return (
    <Box sx={{ padding: 2 }}>
      {/* Admin Panel Button - top right */}
      <Box sx={{ display: "flex", justifyContent: "flex-end", mb: 2 }}>
        <Button variant="contained" color="secondary" onClick={() => setShowAdmin(!showAdmin)}>
          {showAdmin ? "Hide Admin Panel" : "Admin Panel"}
        </Button>
      </Box>

      {/* Admin Panel Modal */}
      <Modal open={showAdmin} onClose={() => setShowAdmin(false)}>
        <Box sx={{ p: 4, bgcolor: "#fff", margin: "5% auto", width: "90%", maxWidth: 600, borderRadius: 2 }}>
          <AdminPanel />
        </Box>
      </Modal>

      {/* View Menu Grid */}
      <Typography variant="h5" gutterBottom>üçñ Mutton Curry Menu</Typography>
      <Grid container spacing={2}>
        {Menu.map((item, index) => (
          <Grid item key={item.id || index} xs={12} sm={6} md={4}>
            <Box sx={{ border: "1px solid #ccc", p: 2, borderRadius: 2 }}>
              <img
                src={item.image || "https://placehold.co/300x180?text=Mutton+Curry"}
                alt={item.name}
                style={{ width: "100%", height: "180px", objectFit: "cover", borderRadius: "5px" }}
              />
              <Typography variant="h6">{item.name}</Typography>
              <Typography variant="body2" color="text.secondary">{item.description}</Typography>
              <Typography variant="subtitle1" sx={{ mt: 1, mb: 1 }}>‚Çπ{item.price}</Typography>
              <Button fullWidth variant="contained" onClick={() => handleOrderClick(item)}>Order Now</Button>
            </Box>
          </Grid>
        ))}
      </Grid>

      {/* Order Form Modal */}
      <Modal open={showOrderForm} onClose={handleCloseForm}>
        <Box sx={{ p: 4, bgcolor: "#fff", margin: "5% auto", width: "90%", maxWidth: 500, borderRadius: 2 }}>
          <OrderForm item={selectedItem} onClose={handleCloseForm} />
        </Box>
      </Modal>
    </Box>
  );
};

export default HomePage;
