import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import logo from '../assets/logo.png';

function Header({ toggleDarkMode, isDarkMode }) {
  const [menuOpen, setMenuOpen] = useState(false);
  const [searchText, setSearchText] = useState('');

  const buttonStyle = {
    backgroundColor: 'transparent',
    color: isDarkMode ? '#fff' : '#8B0000',
    border: 'none',
    padding: '10px 15px',
    cursor: 'pointer',
    fontWeight: 'bold',
    fontSize: '16px',
  };

  const handleSearch = () => {
    // You can implement search logic or navigation
    alert(`Searching for: ${searchText}`);
  };

  return (
    <header
      style={{
        position: 'sticky',
        top: 0,
        zIndex: 1000,
        backgroundColor: isDarkMode ? '#222' : '#fdf9f9ff',
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        padding: '10px 20px',
        boxShadow: '0 2px 8px rgba(0, 0, 0, 0.1)',
        transition: 'background-color 0.5s ease',
        flexWrap: 'wrap',
      }}
    >
      {/* Left: Logo */}
      <div style={{ display: 'flex', alignItems: 'center' }}>
        <img
          src={logo}
          alt="Bharakath Mutton Curry Shop Logo"
          style={{
            height: '50px',
            objectFit: 'contain',
            filter: isDarkMode ? 'brightness(0.85)' : 'none',
          }}
        />
      </div>

      {/* Center: Search Box */}
      <div style={{ display: 'flex', alignItems: 'center', flex: 1, justifyContent: 'center' }}>
        <input
          type="text"
          value={searchText}
          onChange={(e) => setSearchText(e.target.value)}
          placeholder="Search Varieties..."
          style={{
            padding: '8px 12px',
            borderRadius: '5px 0 0 5px',
            border: '1px solid #ccc',
            outline: 'none',
            width: '200px',
          }}
        />
        <button
          onClick={handleSearch}
          style={{
            padding: '8px 15px',
            backgroundColor: isDarkMode ? '#444' : '#8B0000',
            color: '#fff',
            border: 'none',
            borderRadius: '0 5px 5px 0',
            cursor: 'pointer',
          }}
        >
          Search
        </button>
      </div>

      {/* Right: Navigation Buttons */}
      <div style={{ display: 'flex', gap: '15px', alignItems: 'center' }}>
        <Link to="/about"><button style={buttonStyle}>About</button></Link>
        <Link to="/varieties"><button style={buttonStyle}>Varieties</button></Link>
        <Link to="/admin-login"><button style={buttonStyle}>Admin Orders</button></Link>
      </div>
    </header>
  );
}

export default Header;
