import React, { useState } from 'react';
import axios from 'axios';

function OrderForm() {
  const [form, setForm] = useState({
    name: '',
    quantity: '',
    size: 'small',
    item_name: 'Mutton Gravy',
  });

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/order', form);
      alert('‚úÖ Order Placed Successfully!');
      setForm({ name: '', quantity: '', size: 'small', item_name: 'Mutton Gravy' });
    } catch (err) {
      console.error(err);
      alert('‚ùå Failed to place order');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        name="name"
        placeholder="Your Name"
        value={form.name}
        onChange={handleChange}
        required
      />
      <input
        name="quantity"
        type="number"
        placeholder="Quantity (Kg)"
        value={form.quantity}
        onChange={handleChange}
        required
      />
      <select name="size" value={form.size} onChange={handleChange}>
        <option value="small">ü•£ Small</option>
        <option value="bulk">üç≤ Bulk</option>
      </select>

      <select name="item_name" value={form.item_name} onChange={handleChange}>
        <option value="Mutton Gravy">Mutton Gravy</option>
        <option value="Spicy Andhra Curry">Spicy Andhra Curry</option>
        <option value="Mutton Rogan Josh">Mutton Rogan Josh</option>
        <option value="Pepper Mutton">Pepper Mutton</option>
        <option value="Mutton Sukka">Mutton Sukka</option>
        <option value="Chettinad Mutton">Chettinad Mutton</option>
      </select>

      <button type="submit">üõí Place Order</button>
    </form>
  );
}

export default OrderForm;
