import React from 'react';
import { Link } from 'react-router-dom';

function Header() {
  return (
    <header
      style={{
        backgroundColor: '#8B0000',
        color: 'white',
        padding: '15px 20px',
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        flexWrap: 'wrap',
      }}
    >
      <h2 style={{ margin: 0, fontFamily: 'Arial, sans-serif' }}>ðŸ¥˜ Mutton Curry Shop</h2>

      <div style={{ display: 'flex', gap: '10px' }}>
        <Link to="/about">
          <button style={buttonStyle}>About</button>
        </Link>
        <Link to="/varieties">
          <button style={buttonStyle}>Varieties</button>
        </Link>
        <Link to="/admin-login">
          <button style={buttonStyle}>Admin Orders</button>
        </Link>
      </div>
    </header>
  );
}

const buttonStyle = {
  backgroundColor: '#fff',
  color: '#8B0000',
  border: 'none',
  borderRadius: '5px',
  padding: '10px 15px',
  cursor: 'pointer',
  fontWeight: 'bold',
  boxShadow: '0 2px 5px rgba(0,0,0,0.2)',
  transition: 'background 0.3s ease, color 0.3s ease',
};

export default Header;
