// frontend/src/components/OrderTimeline.js
import React from 'react';
import { FaCheckCircle, FaConciergeBell, FaUtensils, FaTruck } from 'react-icons/fa';

const steps = [
  { title: 'Order Placed', icon: <FaConciergeBell />, status: 'completed' },
  { title: 'Preparing', icon: <FaUtensils />, status: 'in-progress' },
  { title: 'Out for Delivery', icon: <FaTruck />, status: 'pending' },
  { title: 'Delivered', icon: <FaCheckCircle />, status: 'pending' },
];

const OrderTimeline = ({ currentStep = 1 }) => {
  return (
    <div style={styles.container}>
      {steps.map((step, index) => {
        const stepStatus =
          index < currentStep ? 'completed' : index === currentStep ? 'in-progress' : 'pending';

        return (
          <div key={index} style={styles.stepWrapper}>
            <div
              style={{
                ...styles.icon,
                backgroundColor:
                  stepStatus === 'completed'
                    ? '#4caf50'
                    : stepStatus === 'in-progress'
                    ? '#ffc107'
                    : '#ddd',
                color: stepStatus === 'pending' ? '#999' : '#fff',
              }}
            >
              {step.icon}
            </div>
            <div style={styles.title}>{step.title}</div>
            {index !== steps.length - 1 && (
              <div
                style={{
                  ...styles.line,
                  backgroundColor:
                    index < currentStep ? '#4caf50' : '#ddd',
                }}
              />
            )}
          </div>
        );
      })}
    </div>
  );
};

const styles = {
  container: {
    display: 'flex',
    alignItems: 'center',
    marginTop: '30px',
    marginBottom: '20px',
    justifyContent: 'center',
    flexWrap: 'wrap',
  },
  stepWrapper: {
    display: 'flex',
    alignItems: 'center',
    position: 'relative',
    textAlign: 'center',
    flexDirection: 'column',
    margin: '0 15px',
  },
  icon: {
    fontSize: '28px',
    padding: '15px',
    borderRadius: '50%',
    marginBottom: '8px',
    transition: 'all 0.3s ease-in-out',
  },
  title: {
    fontSize: '14px',
    fontWeight: 'bold',
    color: '#333',
  },
  line: {
    position: 'absolute',
    top: '22px',
    left: '50%',
    width: '100px',
    height: '4px',
    zIndex: -1,
    transform: 'translateX(50%)',
    transition: 'background-color 0.3s ease-in-out',
  },
};

export default OrderTimeline;
