// src/App.js
import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Header from './components/Header';
import About from './components/About';
import ProductList from './components/ProductList';
import OrderTimeline from './components/OrderTimeline';
import Footer from './components/Footer';
import HowItWorks from './components/HowItWorks';
import Highlights from './components/Highlights';
import OrderPage from './components/OrderPage'; 
import AdminLogin from './components/AdminLogin';
import AdminOrders from './components/AdminOrders';
import OrderStatusPage from './pages/OrderStatusPage';

function App() {
  return (
    <Router>
      <Routes>
        {/* Home Page */}
        <Route
          path="/"
          element={
            <div style={{
              minHeight: '100vh',
              background: 'linear-gradient(135deg, #fff0f0 0%, #f9f9ff 100%)',
              fontFamily: 'Segoe UI, Arial, sans-serif',
              paddingBottom: '60px',
            }}>
              <Header />
              <section style={{
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'center',
                justifyContent: 'center',
                padding: '30px 10px 10px 10px',
                textAlign: 'center',
              }}>
                <h1 style={{
                  fontSize: '2.5rem',
                  fontWeight: 'bold',
                  color: '#B22222',
                  marginBottom: '10px',
                  letterSpacing: '1px',
                }}>Welcome to Bharakath Mutton Curry Shop</h1>
                <p style={{
                  fontSize: '1.2rem',
                  color: '#333',
                  maxWidth: '500px',
                  margin: '0 auto 20px auto',
                }}>
                  Fresh, hygienic, and delicious mutton curries delivered to your doorstep. Explore our varieties and order with ease!
                </p>
                <a href="#products" style={{
                  background: '#B22222',
                  color: '#fff',
                  padding: '12px 28px',
                  borderRadius: '30px',
                  fontWeight: 'bold',
                  fontSize: '1.1rem',
                  textDecoration: 'none',
                  boxShadow: '0 2px 8px rgba(178,34,34,0.15)',
                  marginBottom: '20px',
                  transition: 'background 0.3s',
                  display: 'inline-block',
                }}>Order Now</a>
              </section>
              <Highlights />
              <div id="products">
                <ProductList />
              </div>
              <section style={{
                margin: '30px 0',
                padding: '20px',
                background: '#fff',
                borderRadius: '16px',
                boxShadow: '0 2px 12px rgba(0,0,0,0.07)',
                maxWidth: '900px',
                marginLeft: 'auto',
                marginRight: 'auto',
              }}>
                <HowItWorks />
              </section>
            </div>
          }
        />

        {/* Order Page */}
        <Route path="/how-we-work" element={<OrderTimeline />} />
        <Route path="/productlist" element={<ProductList />} />
        <Route path="/order/:productId" element={<OrderPage />} />
        <Route path="/admin-login" element={<AdminLogin />} />
        <Route path="/admin" element={<AdminOrders />} />
        <Route path="/order-status/:orderId" element={<OrderStatusPage />} />
      </Routes>
      <Footer />
    </Router>
  );
}

export default App;
