// src/components/OrderStatusTimeline.js
import React, { useEffect, useState } from 'react';
import './OrderStatusTimeline.css'; // You’ll create this next

const STATUS_STEPS = ['Order Received', 'Preparing', 'Out for Delivery', 'Delivered'];

const OrderStatusTimeline = ({ currentStatus }) => {
  const currentStep = STATUS_STEPS.indexOf(currentStatus);

  return (
    <div className="timeline-container">
      {STATUS_STEPS.map((step, index) => (
        <div className="timeline-step" key={index}>
          <div
            className={`circle ${index <= currentStep ? 'active' : ''}`}
          >
            {index + 1}
          </div>
          <div
            className={`label ${index <= currentStep ? 'active' : ''}`}
          >
            {step}
          </div>
          {index < STATUS_STEPS.length - 1 && (
            <div
              className={`line ${index < currentStep ? 'active' : ''}`}
            />
          )}
        </div>
      ))}
    </div>
  );
};

export default OrderStatusTimeline;
