import React from 'react';
import { Link } from 'react-router-dom';
import logo from '../assets/logo.png';

function Header({ toggleDarkMode, isDarkMode }) {
  const buttonStyle = {
    backgroundColor: 'transparent',
    color: isDarkMode ? '#fff' : '#8B0000',
    border: 'none',
    padding: '10px 15px',
    cursor: 'pointer',
    fontWeight: 'bold',
    fontSize: '16px',
  };

  return (
    <header className="responsive-header">
      <div className="header-left">
        <img
          src={logo}
          alt="Bharakath Mutton Curry Shop Logo"
          className="header-logo"
          style={{ filter: isDarkMode ? 'brightness(0.85)' : 'none' }}
        />
        <div className="shop-name-animated">
          {Array.from('BHARAKATH MUTTON CURRY SHOP').map((char, i) => (
            <span
              key={i}
              style={{
                opacity: 0,
                animation: `fadeInLetter 0.04s linear forwards`,
                animationDelay: `${i * 0.08}s`,
                marginRight: char === ' ' ? '0.4em' : '0.05em',
                display: 'inline-block',
                fontFamily: 'Segoe UI, Arial, sans-serif',
                fontWeight: 'bold',
                color: '#B22222',
                textShadow: '0 1px 4px rgba(178,34,34,0.10)',
                borderRadius: '3px',
                padding: '0 2px',
              }}
            >
              {char}
            </span>
          ))}
        </div>
      </div>
      <nav className="header-nav">
        <Link to="/productlist"><button style={buttonStyle}>Varieties</button></Link>
        <Link to="/order-history"><button style={buttonStyle}>Order History</button></Link>
        <Link to="/admin-login"><button style={buttonStyle}>Admin Login</button></Link>
      </nav>
    </header>
  );
}


// Add animation and responsive CSS safely for React
if (typeof document !== 'undefined' && !document.getElementById('shop-name-animated-style')) {
  const styleSheet = document.createElement('style');
  styleSheet.id = 'shop-name-animated-style';
  styleSheet.textContent = `
    @keyframes fadeInLetter { to { opacity: 1; } }
    .shop-name-animated span {
      opacity: 0;
      animation: fadeInLetter 0.04s linear forwards;
    }
    .responsive-header {
      position: sticky;
      top: 0;
      z-index: 1000;
      background-color: #fdf9f9ff;
      display: flex;
      justify-content: space-between;
      align-items: center;
      padding: 10px 20px;
      box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
      transition: background-color 0.5s ease;
      flex-wrap: wrap;
    }
    .header-left {
      display: flex;
      align-items: center;
      min-width: 0;
    }
    .header-logo {
      height: 50px;
      object-fit: contain;
      min-width: 50px;
    }
    .shop-name-animated {
      margin-left: 12px;
      font-weight: bold;
      font-size: clamp(1.1rem, 4vw, 2rem);
      color: #B22222;
      letter-spacing: 0.09em;
      display: flex;
      flex-wrap: wrap;
      align-items: center;
      min-width: 120px;
      max-width: 100vw;
      overflow: hidden;
      padding: 2px 8px;
      border-radius: 6px;
      box-sizing: border-box;
      line-height: 1.1;
      transition: font-size 0.3s;
      box-shadow: 0 2px 8px rgba(178,34,34,0.08);
      border-bottom: 3px solid #FFD700;
      background: #fff;
    }
    .header-nav {
      display: flex;
      gap: 0px;
      align-items: center;
    }
    @media (max-width: 600px) {
      .responsive-header {
        flex-direction: column;
        align-items: stretch;
        padding: 8px 4px;
      }
      .header-left {
        justify-content: center;
        margin-bottom: 8px;
      }
      .shop-name-animated {
        font-size: 1.1rem !important;
        padding: 2px 2px;
        min-width: 0;
        border-radius: 4px;
        box-shadow: none;
        border-bottom: 2px solid #FFD700;
      }
      .header-logo {
        height: 38px;
        min-width: 38px;
      }
      .header-nav {
        flex-direction: column;
        gap: 4px;
        align-items: stretch;
      }
      .header-nav button {
        width: 100%;
        font-size: 1rem;
        padding: 10px 0;
        margin-bottom: 2px;
        border-radius: 6px;
      }
    }
  `;
  document.head.appendChild(styleSheet);
}

export default Header;
