import React, { useState } from 'react';
import { Link } from 'react-router-dom';

function Header() {
  const [darkMode, setDarkMode] = useState(false);

  const toggleDarkMode = () => setDarkMode(!darkMode);

  const headerStyle = {
    position: 'sticky',
    top: 0,
    zIndex: 1000,
    backgroundColor: darkMode ? '#222' : '#fdf9f9ff',
    color: darkMode ? '#fff' : '#8B0000',
    padding: '15px 20px',
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    flexWrap: 'wrap',
    boxShadow: '0 2px 5px rgba(0,0,0,0.1)',
    transition: 'background-color 0.3s ease',
  };

  const navLinksStyle = {
    display: 'flex',
    gap: '20px',
  };

  const linkStyle = {
    color: darkMode ? '#fff' : '#8B0000',
    textDecoration: 'none',
    fontWeight: 'bold',
    fontSize: '16px',
    padding: '8px 12px',
    borderRadius: '5px',
  };

  const rightSideStyle = {
    display: 'flex',
    alignItems: 'center',
    gap: '15px',
  };

  return (
    <header style={headerStyle}>
      <div style={navLinksStyle}>
        <Link to="/about" style={linkStyle}>About</Link>
        <Link to="/varieties" style={linkStyle}>Varieties</Link>
        <Link to="/admin-login" style={linkStyle}>Admin Orders</Link>
      </div>

      <div style={rightSideStyle}>
        <img
          src="/logo.png"
          alt="Logo"
          style={{ height: '40px', borderRadius: '50%' }}
        />
        <button
          onClick={toggleDarkMode}
          style={{
            backgroundColor: darkMode ? '#444' : '#ddd',
            color: darkMode ? '#fff' : '#000',
            border: 'none',
            borderRadius: '20px',
            padding: '8px 15px',
            cursor: 'pointer',
            fontWeight: 'bold',
          }}
        >
          {darkMode ? '‚òÄÔ∏è Light' : 'üåô Dark'}
        </button>
      </div>
    </header>
  );
}

export default Header;
