import React, { useState } from 'react';

const AdminPanel = ({ orders }) => {
  const [show, setShow] = useState(false);

  return (
    <div>
      <button onClick={() => setShow(!show)}>
        {show ? 'Hide Admin Panel' : 'üõ†Ô∏è Admin Panel'}
      </button>
      {show && (
        <div style={{ marginTop: '10px', background: '#f4f4f4', padding: '15px' }}>
          <h3>All Orders</h3>
          {orders.length === 0 ? (
            <p>No orders yet.</p>
          ) : (
            <ul>
              {orders.map((order, index) => (
                <li key={index}>
                  {order.name} ordered {order.quantity} x {order.curry} (‚Çπ{order.total})
                </li>
              ))}
            </ul>
          )}
        </div>
      )}
    </div>
  );
};

export default AdminPanel;
