// src/components/ProductList.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const products = [
  { id: 1, name: 'Normal Mutton', price: '‚Çπ800', type: 'Spicy' ,image:"normal.jpg"},
  { id: 2, name: 'BoneLess Mutton', price: '‚Çπ1000', type: 'Gravy' ,image:"boneless.jpg"},
  { id: 3, name: 'Mutton Kudal', price: '‚Çπ280', type: 'Spicy' ,image:"kudal.jpg"},
  { id: 4, name: 'Mutton Thalai', price: '‚Çπ300', type: 'Gravy' ,image:"thalai.jpg"},
  { id: 5, name: 'Mutton Kaal', price: '‚Çπ200', type: 'Soup' ,image:"kaal.jpg"},
];

function ProductList() {
  const navigate = useNavigate();
  const [searchTerm, setSearchTerm] = useState('');

  const handleOrderClick = (id) => {
    navigate(`/order/${id}`);
  };

  const filteredProducts = products.filter((item) =>
    item.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <section style={{ padding: '30px', backgroundColor: '#fff' }}>
      <h2 style={{ textAlign: 'center' }}>Our Mutton Curry Varieties</h2>

      {/* üîç Search Box in Top Center */}
      <div style={{ textAlign: 'center', marginBottom: '20px' }}>
        <input
          type="text"
          placeholder="Search mutton dish..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          style={{
            padding: '10px',
            width: '300px',
            border: '1px solid #ccc',
            borderRadius: '5px',
            fontSize: '16px',
          }}
        />
      </div>

      <div
        style={{
          display: 'flex',
          flexWrap: 'wrap',
          justifyContent: 'center',
          marginTop: '20px',
        }}
      >
        {filteredProducts.map((item) => (
          <div
            key={item.id}
            style={{
              border: '1px solid #ccc',
              borderRadius: '10px',
              padding: '15px',
              margin: '10px',
              width: '220px',
              boxShadow: '0 0 10px rgba(0,0,0,0.1)',
              textAlign: 'center',
            }}
          >
            <h3>{item.name}</h3>
            <p>Type: {item.type}</p>
            <p>
              <strong>Price: {item.price}</strong>
            </p>
            <button
              style={{
                backgroundColor: '#B22222',
                color: 'white',
                padding: '10px 15px',
                border: 'none',
                borderRadius: '5px',
                cursor: 'pointer',
              }}
              onClick={() => handleOrderClick(item.id)}
            >
              Order Now
            </button>
          </div>
        ))}
      </div>
    </section>
  );
}

export default ProductList;
