import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  Grid, Card, CardContent, CardMedia, Typography, Container,
  TextField, CircularProgress
} from '@mui/material';

function ViewMenu() {
  const [menuItems, setMenuItems] = useState([]);
  const [filteredItems, setFilteredItems] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [loading, setLoading] = useState(true);

  // Fetch menu items
  useEffect(() => {
    axios.get('http://localhost:5000/menu')
      .then(res => {
        setMenuItems(res.data);
        setFilteredItems(res.data);
        setLoading(false);
      })
      .catch(err => {
        console.error('‚ùå Error fetching menu:', err);
        setLoading(false);
      });
  }, []);

  // Filter menu items on search
  useEffect(() => {
    const query = searchQuery.toLowerCase();
    const results = menuItems.filter(item =>
      item.name.toLowerCase().includes(query) ||
      item.category.toLowerCase().includes(query)
    );
    setFilteredItems(results);
  }, [searchQuery, menuItems]);

  return (
    <Container sx={{ paddingTop: 4, paddingBottom: 4 }}>
      <Typography variant="h4" gutterBottom align="center">
        üçõ Our Mutton Curry Menu
      </Typography>

      {/* üîç Search Bar */}
      <TextField
        fullWidth
        label="Search by name or category"
        variant="outlined"
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
        sx={{ marginBottom: 4 }}
      />

      {/* üßæ Menu Items */}
      {loading ? (
        <Grid container justifyContent="center">
          <CircularProgress />
        </Grid>
      ) : (
        <Grid container spacing={4}>
          {filteredItems.map(item => (
            <Grid item xs={12} sm={6} md={4} key={item.id}>
              <Card sx={{ height: '100%' }}>
                <CardMedia
                  component="img"
                  height="180"
                  image={item.image_url || 'https://via.placeholder.com/300x180?text=Mutton+Curry'}
                  alt={item.name}
                />
                <CardContent>
                  <Typography variant="h6">{item.name}</Typography>
                  <Typography variant="body2" color="text.secondary">
                    Category: {item.category} | Price: ‚Çπ{item.price}
                  </Typography>
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>
      )}
    </Container>
  );
}

export default ViewMenu;
